#include <vector>
#include <unordered_map>
#include <set>
#include <iostream>
#include <array>
#include <algorithm>
using namespace std;

vector<vector<int>> threeSum(vector<int>& nums) {
    bool AllZero = true;
    for (int i = 0; i < nums.size(); i++) {
        if (nums[i] != 0) {
            AllZero = false;
            break;
        }
    }
    if (AllZero) {
        return {{0, 0, 0}};
    }
    std::sort(nums.begin(), nums.end());
    vector<int> numsCopy = {nums[0]};
    int sameNums = 1;
    for (int i = 1; i < nums.size(); i++) {
        if (nums[i] == nums[i - 1])
            ++sameNums;
        else
            sameNums = 1;
        if (sameNums <= 3) {
            numsCopy.push_back(nums[i]);
        }
    }
    
    unordered_map<int, vector<pair<int, int>>> twoSum;
    for (int i = 0; i < numsCopy.size(); i++) {
        for (int j = i + 1; j < numsCopy.size(); j++) {
            twoSum[numsCopy[i] + numsCopy[j]].push_back({i, j});
        }
    }

    set<array<int, 3>> result;
    for (int i = 0; i < numsCopy.size(); i++) {
        auto it = twoSum.find(-numsCopy[i]);
        if (it != twoSum.end()) {
            for (auto &[j, k]: it->second) {
                if (i != j && i != k) {
                    int a = min(numsCopy[i], min(numsCopy[j], numsCopy[k]));
                    int b = max(numsCopy[i], max(numsCopy[j], numsCopy[k]));
                    int c = numsCopy[i] + numsCopy[j] + numsCopy[k] - a - b;
                    result.insert({a, c, b});
                }
            }
        }
    }
    vector<vector<int>> resultVec;
    for (auto& triplet : result) {
        resultVec.push_back({triplet[0], triplet[1], triplet[2]});
    }
    return resultVec;
}

